import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './Form.css'; // Import the CSS file

const stripePromise = loadStripe('pk_test_51PUvGYDnU0bnacSN8IVpRzur6MRgdxgSumM0E2SmFBGcCY1XieKrADjLJlKImzdIiLmFuaNimjMZPlEsWWlLudAH00v0A0af9I');

const CheckoutForm: React.FC<{ amount: number }> = ({ amount }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setLoading(true);

    if (!stripe || !elements) {
      setError('Stripe has not loaded yet.');
      setLoading(false);
      return;
    }

    const cardElement = elements.getElement(CardElement);

    if (cardElement) {
      const { error, token } = await stripe.createToken(cardElement);

      if (error) {
        setError('pussio');
        setLoading(false);
        return;
      }

      const response = await fetch('https://your-secure-backend.com/create-payment-intent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ token: token.id, amount }),
      });

      if (response.ok) {
        setError(null);
        console.log('Payment successful!');
      } else {
        setError('Payment failed. Please try again.');
      }
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="stripe-form">
      <div className="statement">
        You can easily complete your donation in the form below. All you need is a credit card.
      </div>
      <div className="question">
        Your credit card will be charged ${amount}.
      </div>
      <CardElement className="card-element" />
      {error && <div className="error">{error}</div>}
      <button type="submit" className="ok-button" disabled={loading || !stripe}>
        {loading ? 'Processing...' : 'Submit'}
      </button>
      <div className="stripe-badge">
        <img src="https://stripe.com/img/v3/home/social.png" alt="Powered by Stripe" />
      </div>
    </form>
  );
};

const Slide5: React.FC<{ nextSlide: () => void, amount: number }> = ({ nextSlide, amount }) => {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm amount={amount} />
    </Elements>
  );
};

export default Slide5;
