import React, { useState } from 'react';
import './App.css';
import FormContainer from './Form/FormContainer';

function App() {
  const [isVisible, setIsVisible] = useState(false);

  const showPopup = () => {
    setIsVisible(true);
  };

  const hidePopup = () => {
    setIsVisible(false);
  };

  return (
    <div className="App">
      <button onClick={showPopup} className="donate-button">Donate</button>
      <div className={isVisible ? "popup-container visible" : "popup-container"}>
        <FormContainer onHide={hidePopup} />
      </div>
    </div>
  );
}

export default App;
