import React, { useState } from 'react';
import Slide1 from './slides/Slide1';
import Slide2 from './slides/Slide2';
import Slide3 from './slides/Slide3';
import Slide4 from './slides/Slide4';
import Slide5 from './slides/Slide5';
import bg from './assets/bg.png'; // Import the background image
import './Form.css'; // Import the CSS file

const FormContainer: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [name, setName] = useState('');
  const [lastName, setLastName] = useState('');
  const [progress, setProgress] = useState(0);
  const [selectedAmount, setSelectedAmount] = useState<number | null>(null); // Track the selected amount
  const [animation, setAnimation] = useState('fadeInUp');
  const [nameTyped, setNameTyped] = useState(false); // Added the missing state
  const totalActions = 9; // Total number of actions for full progress

  const incrementProgress = (amount: number) => {
    setProgress(prev => Math.min(prev + amount, 100)); // Ensure progress does not exceed 100
  };

  const decrementProgress = (amount: number) => {
    setProgress(prev => Math.max(prev - amount, 0)); // Ensure progress does not go below 0
  };

  const nextSlide = () => {
    setAnimation('fadeOutUp');
    setTimeout(() => {
      setCurrentSlide(currentSlide + 1);
      setAnimation('fadeInUp');
      incrementProgress(100 / totalActions); // Increment progress for moving to the next page
    }, 500); // Match the duration of the animation
  };

  const prevSlide = () => {
    setAnimation('fadeOutUp');
    setTimeout(() => {
      setCurrentSlide(currentSlide - 1);
      setAnimation('fadeInUp');
    }, 500); // Match the duration of the animation
  };

  const handleNameChange = (name: string) => {
    setName(name);
    if (name.length > 0 && !nameTyped) {
      incrementProgress(100 / totalActions); // Increment progress for typing in an input
      setNameTyped(true);
    } else if (name.length === 0 && nameTyped) {
      decrementProgress(100 / totalActions); // Decrement progress if input is cleared
      setNameTyped(false);
    }
  };

  const handleAmountSelect = (amount: number) => {
    setSelectedAmount(amount);
    incrementProgress(100 / totalActions); // Increment progress for selecting an amount
  };

  const slides = [
    <Slide1 nextSlide={nextSlide} />,
    <Slide2 nextSlide={nextSlide} onNameChange={handleNameChange} />,
    <Slide3 nextSlide={nextSlide} incrementProgress={incrementProgress} decrementProgress={decrementProgress} setLastName={setLastName} />,
    <Slide4 nextSlide={nextSlide} name={name} incrementProgress={incrementProgress} onAmountSelect={handleAmountSelect} />,
    <Slide5 nextSlide={nextSlide} amount={selectedAmount || 0} firstName={name} lastName={lastName} /> // Pass the selected amount to Slide5
    // Add additional slides to the array as needed
  ];

  return (
    <div className="overlay">
      <div className="form-container" style={{ backgroundImage: `url(${bg})` }}>
        <div className="progress-bar">
          <div className="progress" style={{ width: `${progress}%` }}></div>
        </div>
        <div className={`form ${animation}`}>
          {slides[currentSlide]}
        </div>
      </div>
    </div>
  );
};

export default FormContainer;
