import React, { useState } from 'react';
import Slide1 from './Slide1';
import Slide2 from './Slide2';
import Slide3 from './Slide3';
import Slide4 from './Slide4';
import './Form.css'; // Import the CSS file

const FormContainer: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [name, setName] = useState('');
  const [animation, setAnimation] = useState('fadeInUp');

  const nextSlide = () => {
    setAnimation('fadeOutUp');
    setTimeout(() => {
      setCurrentSlide(currentSlide + 1);
      setAnimation('fadeInUp');
    }, 500); // Match the duration of the animation
  };

  const prevSlide = () => {
    setAnimation('fadeOutUp');
    setTimeout(() => {
      setCurrentSlide(currentSlide - 1);
      setAnimation('fadeInUp');
    }, 500); // Match the duration of the animation
  };

  const handleNameChange = (name: string) => {
    setName(name);
  };

  const slides = [
    <Slide1 nextSlide={nextSlide} />,
    <Slide2 nextSlide={nextSlide} onNameChange={handleNameChange} />,
    <Slide3 nextSlide={nextSlide} />,
    <Slide4 nextSlide={nextSlide} name={name} />
    // Add additional slides to the array as needed
  ];

  return (
    <div className="background">
      <div className={`form ${animation}`}>
        {slides[currentSlide]}
      </div>
    </div>
  );
};

export default FormContainer;
