import React, { useState, useEffect } from 'react';

const withAnimation = (WrappedComponent: React.FC<any>) => {
  return (props: any) => {
    const [animation, setAnimation] = useState('fadeInUp');
    const [isAnimating, setIsAnimating] = useState(false);

    const startAnimation = (callback: () => void) => {
      if (!isAnimating) {
        setIsAnimating(true);
        setAnimation('fadeOutUp');
        setTimeout(() => {
          callback();
          setAnimation('fadeInUp');
          setTimeout(() => {
            setIsAnimating(false);
          }, 500); // Match the duration of the animation
        }, 500); // Match the duration of the animation
      }
    };

    return (
      <div className={`form ${animation}`}>
        <WrappedComponent {...props} startAnimation={startAnimation} isAnimating={isAnimating} />
      </div>
    );
  };
};

export default withAnimation;
