import React, { useState } from 'react';
import './App.css';
import FormContainer from './Form/FormContainer'; // Ensure the correct import path

function App() {
  const [isVisible, setIsVisible] = useState(false);

  const showPopup = () => {
    setIsVisible(true);
  };

  const hidePopup = () => {
    setIsVisible(false);
  };

  return (
    <div className="App">
      <button onClick={showPopup} className="donate-button">Donate</button>
      {isVisible && (
        <FormContainer onHide={hidePopup} />
      )}
    </div>
  );
}

export default App;
