import React, { useState } from 'react';
import Slide1 from './Slide1';
import Slide2 from './Slide2';
import Slide3 from './Slide3';
import Slide4 from './Slide4';
import withAnimation from './withAnimation';
import './Form.css'; // Import the CSS file

const AnimatedSlide1 = withAnimation(Slide1);
const AnimatedSlide2 = withAnimation(Slide2);
const AnimatedSlide3 = withAnimation(Slide3);
const AnimatedSlide4 = withAnimation(Slide4);

const FormContainer: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [name, setName] = useState('');
  const [progress, setProgress] = useState(0);
  const [nameTyped, setNameTyped] = useState(false); // Added the missing state
  const [animation, setAnimation] = useState('fadeInUp'); // Define animation state
  const [isAnimating, setIsAnimating] = useState(false); // Define animating state
  const totalActions = 9; // Total number of actions for full progress

  const incrementProgress = (amount: number) => {
    setProgress(prev => Math.min(prev + amount, 100)); // Ensure progress does not exceed 100
  };

  const decrementProgress = (amount: number) => {
    setProgress(prev => Math.max(prev - amount, 0)); // Ensure progress does not go below 0
  };

  const nextSlide = (callback: () => void) => {
    if (!isAnimating) {
      setIsAnimating(true);
      setAnimation('fadeOutUp');
      setTimeout(() => {
        callback();
        setAnimation('fadeInUp');
        setTimeout(() => {
          setIsAnimating(false);
        }, 500); // Match the duration of the animation
      }, 500); // Match the duration of the animation
    }
  };

  const prevSlide = (callback: () => void) => {
    if (!isAnimating) {
      setIsAnimating(true);
      setAnimation('fadeOutUp');
      setTimeout(() => {
        callback();
        setAnimation('fadeInUp');
        setTimeout(() => {
          setIsAnimating(false);
        }, 500); // Match the duration of the animation
      }, 500); // Match the duration of the animation
    }
  };

  const handleNameChange = (name: string) => {
    setName(name);
    if (name.length > 0 && !nameTyped) {
      incrementProgress(100 / totalActions); // Increment progress for typing in an input
      setNameTyped(true);
    } else if (name.length === 0 && nameTyped) {
      decrementProgress(100 / totalActions); // Decrement progress if input is cleared
      setNameTyped(false);
    }
  };

  const slides = [
    <AnimatedSlide1 nextSlide={() => nextSlide(() => setCurrentSlide(currentSlide + 1))} />,
    <AnimatedSlide2 nextSlide={() => nextSlide(() => setCurrentSlide(currentSlide + 1))} onNameChange={handleNameChange} />,
    <AnimatedSlide3 nextSlide={() => nextSlide(() => setCurrentSlide(currentSlide + 1))} />,
    <AnimatedSlide4 nextSlide={() => nextSlide(() => setCurrentSlide(currentSlide + 1))} name={name} />
    // Add additional slides to the array as needed
  ];

  return (
    <div className="background">
      <div className="progress-bar">
        <div className="progress" style={{ width: `${progress}%` }}></div>
      </div>
      {slides[currentSlide]}
    </div>
  );
};

export default FormContainer;
