import React, { useState, useEffect } from 'react';
import rightArrow from '../assets/rightArrow.svg'; // Import the right arrow SVG
import warning from '../assets/error.svg'; // Import the error icon as warning
import '../Form.css'; // Import the CSS file

const Slide6: React.FC<{ nextSlide: () => void, firstName: string }> = ({ nextSlide, firstName }) => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState(''); // State to manage the error message

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(event.target.value);
    if (!event.target.value) {
      setError('Email is required');
    } else {
      setError('');
    }
  };

  const handleSubmit = () => {
    if (!email) {
      setError('Please provide a valid email.');
    } else {
      nextSlide();
    }
  };

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter' && email) {
        handleSubmit();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [email, nextSlide]);

  return (
    <div className="form">
      <div className="question">
        <span className="number">4</span>
        <img src={rightArrow} className="arrow" alt="Right Arrow" />
        Thank you so much {firstName}, we'll send you the receipt by email. What email address should we use?
      </div>
      <div className="input-container">
        <input
          className="input"
          type="email"
          required
          placeholder="name@example.com"
          value={email}
          onChange={handleChange}
        />
        {error && (
          <div className="error-message" style={{ display: 'flex', alignItems: 'center' }}>
            <img src={warning} alt="Warning" style={{ width: '20px', height: '20px', marginRight: '5px' }} />
            {error}
          </div>
        )}
        <button className="ok-button" onClick={handleSubmit}>OK</button>
      </div>
    </div>
  );
};

export default Slide6;
