import React, { useState } from 'react';
import './App.css';

function App() {
  const [isVisible, setIsVisible] = useState(false);

  const showPopup = () => {
    setIsVisible(true);
  };

  const hidePopup = () => {
    setIsVisible(false);
  };

  return (
    <div className="App">
      <button onClick={showPopup} className="donate-button">Donate</button>
      {isVisible && (
        <>
          <div className="overlay" onClick={hidePopup}></div> {/* Click on the overlay to hide popup */}
          <div className="popup">
            <form className="payment-form">
              <input type="text" placeholder="Name on Card" />
              <input type="text" placeholder="Card Number" />
              <input type="text" placeholder="CVV" />
              <input type="text" placeholder="Expiry Date" />
              <button type="submit">Submit Payment</button>
            </form>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
