import React, { useEffect, useState } from 'react';
import rightArrow from './rightArrow.svg'; // Import the right arrow SVG
import './Form.css'; // Import the CSS file

const Slide4: React.FC<{ nextSlide: () => void, name: string }> = ({ nextSlide, name }) => {
  const [amount, setAmount] = useState('');
  const [amountTyped, setAmountTyped] = useState(false);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setAmount(event.target.value);
    if (event.target.value.length > 0 && !amountTyped) {
      // Increment progress if amount is typed for the first time
      // Note: This requires an increment function passed down as a prop if needed
      setAmountTyped(true);
    } else if (event.target.value.length === 0 && amountTyped) {
      // Decrement progress if input is cleared
      // Note: This requires a decrement function passed down as a prop if needed
      setAmountTyped(false);
    }
  };

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter') {
        nextSlide();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [nextSlide]);

  return (
    <div className="form">
      <div className="question">
        <span className="number">2</span>
        <img src={rightArrow} className="arrow" alt="Right Arrow" />
        We're very grateful you're here to donate, {name}. How much are you willing to give today?
      </div>
      <div className="description">in $USD</div>
      <div className="input-container">
        <input
          className="input"
          type="text"
          placeholder="Type your answer here..."
          value={amount}
          onChange={handleChange}
        />
        <button className="ok-button" onClick={nextSlide}>OK</button>
      </div>
    </div>
  );
};

export default Slide4;
