import React from 'react';
import bg from './bg.png'; // Import the background image

const Form: React.FC = () => {

    const backgroundStyle: React.CSSProperties = {
        backgroundImage: `url(${bg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        width: '100%',
        height: '100vh', // Full viewport height
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: '#fff', // Text color for visibility
        padding: '20px', // Add some padding to prevent content from touching the edges
        boxSizing: 'border-box' as 'border-box', // Correctly type boxSizing
      };

  const formStyle: React.CSSProperties = {
    width: '100%',
    maxWidth: '500px',
    textAlign: 'left',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    color: '#000', // Assuming black text color
  };

  const questionStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  };


  const companyStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  };

  const inputStyle: React.CSSProperties = {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  };

  return (
    <div style={backgroundStyle}>
        <div style={formStyle}>
            <div style={questionStyle}>And finally, would you like to receive updates about what we're able to do thanks to donations like yours?</div>
            <div style={companyStyle}>Company</div>
            <input type="text" placeholder="Acme Corporation" style={inputStyle} />
        </div>
    </div>
  );
};

export default Form;
