import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

declare global {
  interface Window {
    renderPopup: (targetId: string) => void;
  }
}

function renderPopup(targetId: string) {
  const rootElement = document.getElementById(targetId);
  if (rootElement) {
    const root = ReactDOM.createRoot(rootElement);
    root.render(<App />);
  } else {
    console.error(`Element with id ${targetId} not found`);
  }
}

// Expose the render function globally
window.renderPopup = renderPopup;
