import React, { useEffect } from 'react';
import enterIcon from './enter.svg'; // Import the SVG icon
import './Form.css'; // Import the CSS file

const Slide1: React.FC<{ nextSlide: () => void }> = ({ nextSlide }) => {
  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter') {
        nextSlide();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [nextSlide]);

  return (
    <div className="form">
      <div className="question">Your generosity is what keeps us going.</div>
      <div className="answer">
        <span className="text invs">
          press <span className="bold">Enter</span>
        </span>
        <button className="button" onClick={nextSlide}>Donate now</button>
        <span className="text" onClick={nextSlide}>
          press <span className="bold">Enter</span>
        </span>
        <img src={enterIcon} width="20px" height="20px" alt="Enter icon" />
      </div>
    </div>
  );
};

export default Slide1;
