import React from 'react';
import bg from './bg.png'; // Import the background image
import enterIcon from './enter.svg'; // Import the SVG icon
import './Form.css'; // Import the CSS file

const Form: React.FC = () => {
  const backgroundStyle: React.CSSProperties = {
    backgroundImage: `url(${bg})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '100%',
    height: '100vh', // Full viewport height
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: '#fff', // Text color for visibility
    boxSizing: 'border-box' as 'border-box', // Correctly type boxSizing
  };

  const formStyle: React.CSSProperties = {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Hanken Grotesk, sans-serif',
    color: '#000', // Assuming black text color
  };

  const questionStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '40px',
    fontWeight: 'bold',
    paddingBottom: '40px',
  };

  const buttonStyle: React.CSSProperties = {
    padding: '10px 20px',
    fontSize: '30px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    backgroundColor: '#000', // Optional: button background color
    color: '#fff', // Optional: button text color
    fontFamily: 'Hanken Grotesk, sans-serif',
  };

  const answerStyle: React.CSSProperties = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: '20px', // Add margin to the top for spacing
  };

  const textStyle: React.CSSProperties = {
    fontSize: '20px',
    marginRight: '5px',
  };

  const boldTextStyle: React.CSSProperties = {
    fontWeight: 'bold',
  };

  return (
    <div style={backgroundStyle}>
      <div style={formStyle}>
        <div style={questionStyle}>Your generosity is what keeps us going.</div>
        <div style={answerStyle}>
          <span style={textStyle} className='invs'>
            press <span style={boldTextStyle}>Enter</span>
          </span>
          <button style={buttonStyle}>Donate now</button>
          <span style={textStyle}>
            press <span style={boldTextStyle}>Enter</span>
          </span>
          <img src={enterIcon} width="20px" height="20px" alt="Enter icon" />
        </div>
      </div>
    </div>
  );
};

export default Form;
