import React from 'react';
import bg from './bg.png'; // Import the background image

const Form: React.FC = () => {
  const backgroundStyle: React.CSSProperties = {
    backgroundImage: `url(${bg})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '100%',
    height: '100vh', // Full viewport height
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: '#fff', // Text color for visibility
    boxSizing: 'border-box' as 'border-box', // Correctly type boxSizing
  };

  const formStyle: React.CSSProperties = {
    width: '100%',
    maxWidth: '500px',
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Hanken Grotesk, sans-serif',
    color: '#000', // Assuming black text color
    backgroundColor: 'rgba(255, 255, 255, 0.8)', // Optional: white background with opacity for better readability
    borderRadius: '8px', // Optional: rounded corners
  };

  const questionStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  };

  const buttonStyle: React.CSSProperties = {
    padding: '10px 20px',
    fontSize: '16px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  };

  return (
    <div style={backgroundStyle}>
      <div style={formStyle}>
        <div style={questionStyle}>Your generosity is what keeps us going</div>
        <button style={buttonStyle}>Donate now</button>
      </div>
    </div>
  );
};

export default Form;
