import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

declare global {
  interface Window {
    showPopup: () => void;
  }
}

let root: ReactDOM.Root | null = null;

function showPopup() {
  const rootElement = document.getElementById('popup-container');
  if (rootElement) {
    if (!root) {
      root = ReactDOM.createRoot(rootElement);
    }
    root.render(<App />);
  } else {
    console.error('Element with id "popup-container" not found');
  }
}

// Expose the function globally
window.showPopup = showPopup;
