import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

declare global {
  interface Window {
    renderPopup: () => void;
  }
}

function renderPopup() {
  const rootElement = document.getElementById('popup-container');
  if (rootElement) {
    const root = ReactDOM.createRoot(rootElement);
    root.render(<App />);
  } else {
    console.error('Element with id "popup-container" not found');
  }
}

// Expose the function globally
window.renderPopup = renderPopup;
