import React, { useEffect } from 'react';
import rightArrow from './rightArrow.svg'; // Import the right arrow SVG
import '../Form.css'; // Import the CSS file

const Slide5: React.FC<{ nextSlide: () => void, amount: number }> = ({ nextSlide, amount }) => {
  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter') {
        handleSubmit();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, []);

  const handleSubmit = async () => {
    const response = await fetch('http://localhost:4242/create-checkout-session', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ amount }),
    });

    const session = await response.json();

    const stripe = (window as any).Stripe('your-publishable-key-here'); // Add `as any` to avoid TypeScript error
    await stripe.redirectToCheckout({ sessionId: session.id });
  };

  return (
    <div className="form">
      <div className="statement">You can easily complete your donation in the form below. All you need is a credit card.</div>
      <div className="question">
        Your credit card will be charged ${amount}.
      </div>
      <div className="input-container">
        <label>
          <span>Name on card</span>
          <input className="input" type="text" placeholder="Jane Smith" />
        </label>
        <label>
          <span>Card number</span>
          <input className="input" type="text" placeholder="Card number" />
        </label>
        <div className="row">
          <label className="half">
            <span>Expiry date</span>
            <input className="input" type="text" placeholder="MM / YY" />
          </label>
          <label className="half">
            <span>Security code</span>
            <input className="input" type="text" placeholder="CVC" />
          </label>
        </div>
        <button className="ok-button" onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default Slide5;
