import React, { useEffect, useState } from 'react';
import rightArrow from './rightArrow.svg'; // Import the right arrow SVG
import './Form.css'; // Import the CSS file

const Slide4: React.FC<{ nextSlide: () => void, name: string, incrementProgress: (amount: number) => void }> = ({ nextSlide, name, incrementProgress }) => {
  const [selectedAmount, setSelectedAmount] = useState<number | null>(null);
  const [progressUpdated, setProgressUpdated] = useState(false); // Track if progress has been updated
  const amounts = [25, 50, 100, 250, 500];
  const totalActions = 9; // Ensure this matches the total actions in FormContainer

  const handleAmountClick = (amount: number) => {
    setSelectedAmount(amount);
    if (!progressUpdated) {
      incrementProgress(100 / totalActions); // Increment progress when a button is clicked
      setProgressUpdated(true); // Mark progress as updated
    }
  };

  const handleSubmit = () => {
    if (selectedAmount !== null) {
      nextSlide();
    }
  };

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter' && selectedAmount !== null) {
        nextSlide();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [selectedAmount, nextSlide]);

  return (
    <div className="form">
      <div className="question">
        <span className="number">3</span>
        <img src={rightArrow} className="arrow" alt="Right Arrow" />
        We're very grateful you're here to donate, {name}. How much are you willing to give today?
      </div>
      <div className="description">in $USD</div>
      <div className="multiple-choice">
        {amounts.map(amount => (
          <button
            key={amount}
            className={`amount-button ${selectedAmount === amount ? 'selected' : ''}`}
            onClick={() => handleAmountClick(amount)}
          >
            {amount}
          </button>
        ))}
      </div>
      <button className="ok-button" onClick={handleSubmit}>OK</button>
    </div>
  );
};

export default Slide4;
