import React, { useCallback, useEffect, useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, EmbeddedCheckoutProvider, EmbeddedCheckout } from '@stripe/react-stripe-js';
import rightArrow from '../assets/rightArrow.svg';
import '../Form.css'; // Import the CSS file

const stripePromise = loadStripe('pk_test_51PUvGYDnU0bnacSN8IVpRzur6MRgdxgSumM0E2SmFBGcCY1XieKrADjLJlKImzdIiLmFuaNimjMZPlEsWWlLudAH00v0A0af9I');

const CheckoutForm: React.FC<{ amount: number; firstName: string; lastName: string }> = ({ amount, firstName, lastName }) => {
  const [clientSecret, setClientSecret] = useState<string | null>(null);

  const fetchClientSecret = useCallback(async () => {
    try {
      const response = await fetch('http://localhost:4242/create-checkout-session', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: amount * 100 }), // Stripe expects amount in cents
      });
      const data = await response.json();
      console.log("Fetched clientSecret:", data.clientSecret); // Debug log
      setClientSecret(data.clientSecret);
    } catch (error) {
      console.error("Error fetching clientSecret:", error);
    }
  }, [amount]);

  useEffect(() => {
    fetchClientSecret();
  }, [fetchClientSecret]);

  const options = { clientSecret };

  console.log("Rendering CheckoutForm with clientSecret:", clientSecret); // Debug log

  return (
    <div className="stripe-form">
      <div className="question">
        <span className="number">3</span>
        <img src={rightArrow} className="arrow" alt="Right Arrow" />
        You can easily complete your donation in the form below. All you need is a credit card.
      </div>
      <div className="description">
        Your credit card will be charged ${amount}.
      </div>
      {clientSecret && (
        <EmbeddedCheckoutProvider stripe={stripePromise} options={{ clientSecret }}>
          <EmbeddedCheckout />
        </EmbeddedCheckoutProvider>
      )}
    </div>
  );
};

const Slide5: React.FC<{ nextSlide: () => void; amount: number; firstName: string; lastName: string }> = ({ nextSlide, amount, firstName, lastName }) => {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm amount={amount} firstName={firstName} lastName={lastName} />
    </Elements>
  );
};

export default Slide5;
