import React, { useState, useEffect } from 'react';
import rightArrow from './rightArrow.svg';
import './Form.css'; // Import the CSS file

const Slide5: React.FC<{ nextSlide: () => void, amount: number }> = ({ nextSlide, amount }) => {
  const [cardDetails, setCardDetails] = useState({
    name: '',
    number: '',
    expiry: '',
    cvc: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCardDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = () => {
    // Assuming nextSlide() will handle form submission and validation
    nextSlide();
  };

  useEffect(() => {
    const handleKeyPress = (event: KeyboardEvent) => {
      if (event.key === 'Enter') {
        handleSubmit();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [handleSubmit]);

  return (
    <div className="form">
       <div className="question">
        <span className="number">3</span>
        <img src={rightArrow} className="arrow" alt="Right Arrow" />
       Complete your donation, all you need is a credit card.
      </div> 
     <div className='description'>
        Your credit card will be charged ${amount}.
    </div>

      <div className="credit-card-container">
        <div className="credit-card-input">
          <label className="credit-card-label" htmlFor="name">Name on card</label>
          <input
            className="credit-card-field"
            type="text"
            name="name"
            id="name"
            value={cardDetails.name}
            onChange={handleChange}
            placeholder="Jane Smith"
          />
        </div>
        <div className="credit-card-input">
          <label className="credit-card-label" htmlFor="number">Card number</label>
          <input
            className="credit-card-field"
            type="text"
            name="number"
            id="number"
            value={cardDetails.number}
            onChange={handleChange}
            placeholder="Card number"
          />
        </div>
        <div className="credit-card-row">
          <div className="credit-card-input">
            <label className="credit-card-label" htmlFor="expiry">Expiry date</label>
            <input
              className="credit-card-field"
              type="text"
              name="expiry"
              id="expiry"
              value={cardDetails.expiry}
              onChange={handleChange}
              placeholder="MM / YY"
            />
          </div>
          <div className="credit-card-input">
            <label className="credit-card-label" htmlFor="cvc">Security code</label>
            <input
              className="credit-card-field"
              type="text"
              name="cvc"
              id="cvc"
              value={cardDetails.cvc}
              onChange={handleChange}
              placeholder="CVC"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slide5;
