import React, { useState } from 'react';
import Slide1 from './slides/Slide1';
import Slide2 from './slides/Slide2';
import Slide3 from './slides/Slide3';
import Slide4 from './slides/Slide4';
import Slide5 from './slides/Slide5';
import Slide6 from './slides/Slide6';
import bg from './assets/bg.png'; // Import the background image
import logo from './assets/logo.png';
import exit from './assets/exit.svg';
import './Form.css'; // Import the CSS file
import Slide7 from './slides/Slide7';

const FormContainer: React.FC<{ onHide: () => void }> = ({ onHide }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [name, setName] = useState('');
  const [lastName, setLastName] = useState('');
  const [progress, setProgress] = useState(0);
  const [selectedAmount, setSelectedAmount] = useState<number | null>(null); // Track the selected amount
  const [animation, setAnimation] = useState('fadeInUp');
  const [nameTyped, setNameTyped] = useState(false); // Added the missing state
  const totalActions = 1; // Total number of actions for full progress

  const incrementProgress = (amount: number) => {
    setProgress(prev => Math.min(prev + amount, 100));
  };

  const decrementProgress = (amount: number) => {
    setProgress(prev => Math.max(prev - amount, 0));
  };

  const nextSlide = () => {
    if (currentSlide < slides.length - 1) {
      incrementProgress(100 / totalActions)
      setAnimation('fadeOutUp');
      setTimeout(() => {
        setCurrentSlide(currentSlide + 1);
        setAnimation('fadeInUp');
      }, 500);
    } else {
      // Fade out and remove the form container when on the last slide
      setTimeout(onHide); // This will call the onHide function passed from App
    }
  };

  const handleExitClick = () => {
    setTimeout(onHide);
  };
  const handleContainerClick = (event: { stopPropagation: () => void; }) => {
    event.stopPropagation(); // This stops the click event from bubbling up to the overlay
  };

  const prevSlide = () => {
    setAnimation('fadeOutUp');
    setTimeout(() => {
      setCurrentSlide(currentSlide - 1);
      setAnimation('fadeInUp');
    }, 500);
  };

  const handleNameChange = (name: string) => {
    setName(name);
    if (name.length > 0 && !nameTyped) {
      incrementProgress(100 / totalActions);
      setNameTyped(true);
    } else if (name.length === 0 && nameTyped) {
      decrementProgress(100 / totalActions);
      setNameTyped(false);
    }
  };

  const handleAmountSelect = (amount: number) => {
    setSelectedAmount(amount);
    incrementProgress(100 / totalActions);
  };

  const slides = [
    <Slide1 nextSlide={nextSlide} />,
    <Slide2 nextSlide={nextSlide} onNameChange={handleNameChange} />,
    <Slide3 nextSlide={nextSlide} incrementProgress={incrementProgress} decrementProgress={decrementProgress} setLastName={setLastName} />,
    <Slide4 nextSlide={nextSlide} name={name} incrementProgress={incrementProgress} onAmountSelect={handleAmountSelect} />,
    <Slide5 nextSlide={nextSlide} amount={selectedAmount || 0} />,
    <Slide6 nextSlide={nextSlide} firstName={name} incrementProgress={incrementProgress} />,
    <Slide7 nextSlide={nextSlide} />
  ];

  return (
    <div className="overlay" onClick={handleExitClick}>
      <div className="form-container" onClick={handleContainerClick} style={{ backgroundImage: `url(${bg})` }}>
        <div className="progress-bar">
          <div className="progress" style={{ width: `${progress}%` }}></div>
        </div>
        <div className='logo-container'>
          <img src={logo} alt="Logo" id='logo' style={{ position: 'absolute', top: '40px', left: '30px', width: '125px' }} />
          <img src={exit} alt="exit" onClick={handleExitClick} className='exit'  style={{ position: 'absolute', top: '55.525px', right: '30px', width: '20px'}} />
        </div>
        <div className={`form ${animation}`}>
          {slides[currentSlide]}
        </div>
      </div>
    </div>
  );
};

export default FormContainer;
