import React from 'react';

const Form: React.FC = () => {
  const formStyle: React.CSSProperties = {
    width: '100%',
    maxWidth: '500px',
    textAlign: 'left',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    color: '#000', // Assuming black text color
  };

  const questionStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  };

  const descriptionStyle: React.CSSProperties = {
    marginBottom: '20px',
    fontSize: '14px',
    color: '#888',
    fontStyle: 'italic',
  };

  const companyStyle: React.CSSProperties = {
    marginBottom: '10px',
    fontSize: '16px',
    fontWeight: 'bold',
  };

  const inputStyle: React.CSSProperties = {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  };

  return (
    <div style={formStyle}>
      <div style={questionStyle}>And finally, would you like to receive updates about what we're able to do thanks to donations like yours?</div>
      <div style={descriptionStyle}>Description (optional)</div>
      <div style={companyStyle}>Company</div>
      <input type="text" placeholder="Acme Corporation" style={inputStyle} />
    </div>
  );
};

export default Form;
