import React, { useState } from 'react';
import { injectStripe, CardElement, Elements, StripeProvider } from 'react-stripe-elements';
import './Form.css'; // Import the CSS file

const CheckoutForm: React.FC<{ amount: number, stripe: any }> = ({ amount, stripe }) => {
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setLoading(true);

    const { token, error } = await stripe.createToken();
    if (error) {
      setError(error.message);
      setLoading(false);
      return;
    }

    const response = await fetch('https://your-secure-backend.com/create-payment-intent', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token: token.id, amount }),
    });

    if (response.ok) {
      setError(null);
      console.log('Payment successful!');
    } else {
      setError('Payment failed. Please try again.');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="stripe-form">
      <div className="statement">
        You can easily complete your donation in the form below. All you need is a credit card.
      </div>
      <div className="question">
        Your credit card will be charged ${amount}.
      </div>
      <CardElement className="card-element" />
      {error && <div className="error">{error}</div>}
      <button type="submit" className="ok-button" disabled={loading || !stripe}>
        {loading ? 'Processing...' : 'Submit'}
      </button>
    </form>
  );
};

const InjectedCheckoutForm = injectStripe(CheckoutForm);

const Slide5: React.FC<{ nextSlide: () => void, amount: number }> = ({ nextSlide, amount }) => {
  return (
    <StripeProvider apiKey="pk_test_51PUvGYDnU0bnacSN8IVpRzur6MRgdxgSumM0E2SmFBGcCY1XieKrADjLJlKImzdIiLmFuaNimjMZPlEsWWlLudAH00v0A0af9I">
      <Elements>
        <InjectedCheckoutForm amount={amount} />
      </Elements>
    </StripeProvider>
  );
};

export default Slide5;
